// frontend/src/components/WeatherAlertCard.jsx
import React, { useState } from 'react';
import './Card.css';

const WeatherAlertCard = ({ onSend, readyState }) => {
  const [location, setLocation] = useState('市中心');
  const [alertType, setAlertType] = useState('heavy_rain');
  const [severity, setSeverity] = useState(8);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (onSend) {
      onSend({ location, alert_type: alertType, severity });
    }
  };

  return (
    <div className="card">
      <h2 className="card__title">天气预警</h2>
      <form className="form" onSubmit={handleSubmit}>
        <label>
          <span>位置</span>
          <input type="text" name="location" value={location} onChange={(e) => setLocation(e.target.value)} />
        </label>
        <label>
          <span>预警类型</span>
          <select value={alertType} onChange={(e) => setAlertType(e.target.value)}>
            <option value="heavy_rain">暴雨</option>
            <option value="strong_wind">强风</option>
            <option value="snow_storm">暴雪</option>
            <option value="heat_wave">热浪</option>
          </select>
        </label>
        <label>
          <span>严重程度</span>
          <input type="number" name="severity" min="1" max="10" value={severity} onChange={(e) => setSeverity(parseInt(e.target.value, 10))} />
        </label>
        <button type="submit" className="btn">发送</button>
      </form>
    </div>
  );
};

export default WeatherAlertCard;
