import React, { useState, useEffect } from 'react';
import { Paper } from '@mui/material';
import AutonomousDrivingCard from '../components/AutonomousDrivingCard';
import WeatherAlertCard from '../components/WeatherAlertCard';
import ParkingUpdateCard from '../components/ParkingUpdateCard';
import SafetyInspectionCard from '../components/SafetyInspectionCard';
import InteractionHistory from '../components/InteractionHistory';
import WebSocketService from '../components/WebSocketService';
import './HomePage.css';

const HomePage = () => {
  const [messages, setMessages] = useState([]);
  const [readyState, setReadyState] = useState(WebSocket.CONNECTING);

  useEffect(() => {
    const wsUrl = `http://localhost:${process.env.REACT_APP_BACKEND_PORT || 8006}`;
    WebSocketService.connect(wsUrl);

    const handleConnect = () => {
      setReadyState(WebSocket.OPEN);
    };

    const handleDisconnect = () => {
      setReadyState(WebSocket.CLOSED);
    };

    const handleMessage = (data) => {
      try {
        // Assuming data is a JSON string
        const message = JSON.parse(data);
        setMessages((prevMessages) => [...prevMessages, message]);
      } catch (error) {
        // If data is not a JSON string, handle it as a raw message
        setMessages((prevMessages) => [...prevMessages, { type: 'raw', data: data, timestamp: new Date().toISOString() }]);
      }
    };

    WebSocketService.on('connect', handleConnect);
    WebSocketService.on('disconnect', handleDisconnect);
    WebSocketService.on('message', handleMessage);

    // Cleanup on component unmount
    return () => {
      WebSocketService.disconnect();
    };
  }, []);

  const handleSend = (task, data) => {
    const message = {
      type: task,
      data: data,
    };
    WebSocketService.send(message);
  };

  const sendGenerateReport = () => {
    const message = {
      type: 'generate_report',
      data: {},
    };
    WebSocketService.send(message);
  };

  const clearHistory = () => {
    setMessages([]);
  };

  return (
    <div className="homepage">
      <header className="homepage__header">
        <h1 className="homepage__title">Multi-Agent Smart City</h1>
        <p className="homepage__subtitle">A DSL-based simulation environment</p>
      </header>
      <main className="homepage__main">
        <div className="interaction-history-container">
          <Paper sx={{ p: 2, display: 'flex', flexDirection: 'column', backgroundColor: 'transparent' }} elevation={0}>
            <InteractionHistory events={messages} readyState={readyState} onGenerateReport={sendGenerateReport} onClear={clearHistory} />
          </Paper>
        </div>
        <div className="card-grid-container">
          <div className="card-grid">
            <AutonomousDrivingCard onSend={(data) => handleSend('autonomous_driving_task', data)} readyState={readyState} />
            <WeatherAlertCard onSend={(data) => handleSend('weather_alert_task', data)} readyState={readyState} />
            <ParkingUpdateCard onSend={(data) => handleSend('parking_update_task', data)} readyState={readyState} />
            <SafetyInspectionCard onSend={(data) => handleSend('safety_inspection_task', data)} readyState={readyState} />
          </div>
        </div>
      </main>
    </div>
  );
};

export default HomePage;
