import React, { useState, useMemo, useEffect } from "react";
import axios from "axios";
import "./App.css";

const API_BASE =
  process.env.REACT_APP_API_BASE?.replace(/\/+$/, "") || "http://127.0.0.1:8000";

export default function App() {
  const [location, setLocation] = useState("");
  const [trafficCondition, setTrafficCondition] = useState("normal");
  const [incidentDetected, setIncidentDetected] = useState(false);
  const [vehicleSpeed, setVehicleSpeed] = useState("");
  const [loading, setLoading] = useState(false);
  const [resp, setResp] = useState(null); // { ok: boolean, message: string }
  const [history, setHistory] = useState([]); // 最近发送记录

  // 其它三个模块的状态
  const [weatherAlert, setWeatherAlert] = useState("");
  const [parkingLocation, setParkingLocation] = useState("");
  const [availableSpots, setAvailableSpots] = useState("");
  const [safetyStatus, setSafetyStatus] = useState("");

  // 交互记录区域高度（可拖拽）
  const [historyHeight, setHistoryHeight] = useState(260);

  // 发送驾驶任务的 payload
  const payload = useMemo(
    () => ({
      location: location.trim(),
      traffic_condition: trafficCondition,
      incident_detected: incidentDetected,
      vehicle_speed: vehicleSpeed === "" ? null : Number(vehicleSpeed),
      ts: Date.now(),
    }),
    [location, trafficCondition, incidentDetected, vehicleSpeed]
  );

  const isValid = useMemo(() => {
    if (!payload.location) return false;
    if (payload.vehicle_speed === null || Number.isNaN(payload.vehicle_speed))
      return false;
    if (payload.vehicle_speed < 0) return false;
    return true;
  }, [payload]);

  // 发送驾驶事件
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!isValid) {
      setResp({ ok: false, message: "请完整填写表单并确保速度为非负数。" });
      return;
    }
    setLoading(true);
    setResp(null);
    try {
      const { data } = await axios.post(`${API_BASE}/events/autonomous_driving`, {
        location: payload.location,
        traffic_condition: payload.traffic_condition,
        incident_detected: payload.incident_detected,
        vehicle_speed: payload.vehicle_speed,
      });
      const message = typeof data === "object" && data?.status ? String(data.status) : "success";
      setResp({ ok: true, message });
      setHistory((h) =>
        [
          {
            id: cryptoRandomId(),
            at: new Date().toLocaleString(),
            ...payload,
            message,
          },
          ...h,
        ].slice(0, 100)
      );
    } catch (err) {
      setResp({
        ok: false,
        message:
          err?.response?.data?.detail
            ? `后端返回: ${JSON.stringify(err.response.data.detail)}`
            : "请求失败，请检查后端是否已启动或接口路径是否正确。",
      });
    } finally {
      setLoading(false);
    }
  };

  // WebSocket：接收后端广播
  useEffect(() => {
    const ws = new WebSocket(`${API_BASE.replace("http", "ws")}/ws`);
    ws.onmessage = (event) => {
      const data = JSON.parse(event.data);

      const push = (payload, message) =>
        setHistory((h) => [{ id: cryptoRandomId(), at: new Date().toLocaleString(), ...payload, message }, ...h].slice(0, 100));

      if (data.type === "autonomous_driving") push(data.payload, "received");
      if (data.type === "weather_alert") push(data.payload, "alert received");
      if (data.type === "parking_update") push(data.payload, "parking updated");
      if (data.type === "safety_monitor") push(data.payload, "safety monitoring");
    };
    return () => ws.close();
  }, []);
<main className="page__main">
  {/* 左侧：四个功能模块（2×2） */}
  <section className="modules">
    {/* 发送任务 */}
    <section className="card">
      <h2 className="card__title">发送任务</h2>
      <form onSubmit={handleSubmit} className="form">
        <div className="form__row">
          <label>
            <span>Location</span>
            <input
              type="text"
              value={location}
              onChange={(e) => setLocation(e.target.value)}
              placeholder="如：Main St."
              required
            />
          </label>

          <label>
            <span>Traffic Condition</span>
            <select
              value={trafficCondition}
              onChange={(e) => setTrafficCondition(e.target.value)}
            >
              <option value="normal">Normal</option>
              <option value="heavy">Heavy</option>
            </select>
          </label>
        </div>

        <div className="form__row">
          <label className="checkbox">
            <input
              type="checkbox"
              checked={incidentDetected}
              onChange={(e) => setIncidentDetected(e.target.checked)}
            />
            <span>Incident Detected</span>
          </label>

          <label>
            <span>Vehicle Speed (km/h)</span>
            <input
              type="number"
              min="0"
              value={vehicleSpeed}
              onChange={(e) => setVehicleSpeed(e.target.value)}
              placeholder="例如 40"
              required
            />
          </label>
        </div>

        <button type="submit" className="btn" disabled={loading || !isValid}>
          {loading ? "Sending..." : "Send"}
        </button>
      </form>

      {resp && (
        <div className={`alert ${resp.ok ? "ok" : "err"}`}>
          <strong>Response:</strong> {resp.message}
        </div>
      )}
    </section>

    {/* 天气告警 */}
    <section className="card">
      <h2 className="card__title">天气告警</h2>
      <form onSubmit={handleWeatherAlertSubmit} className="form">
        <label>
          <span>Alert Type</span>
          <input
            type="text"
            value={weatherAlert}
            onChange={(e) => setWeatherAlert(e.target.value)}
            placeholder="例如 暴雨 / 大风 / 高温 / 雷电 / 冰雹"
            required
          />
        </label>
        <label>
          <span>Area</span>
          <input
            type="text"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            placeholder="例如 北京"
            required
          />
        </label>
        <button type="submit" className="btn" disabled={loading}>
          {loading ? "Sending..." : "Send Alert"}
        </button>
      </form>
    </section>

    {/* 停车位更新 */}
    <section className="card">
      <h2 className="card__title">停车位更新</h2>
      <form onSubmit={handleParkingUpdateSubmit} className="form">
        <label>
          <span>Location</span>
          <input
            type="text"
            value={parkingLocation}
            onChange={(e) => setParkingLocation(e.target.value)}
            placeholder="停车地点"
            required
          />
        </label>
        <label>
          <span>Available Spots</span>
          <input
            type="number"
            min="0"
            value={availableSpots}
            onChange={(e) => setAvailableSpots(e.target.value)}
            placeholder="可用车位数"
            required
          />
        </label>
        <button type="submit" className="btn" disabled={loading}>
          {loading ? "Sending..." : "Update Parking"}
        </button>
      </form>
    </section>

    {/* 安全巡检 */}
    <section className="card">
      <h2 className="card__title">安全巡检</h2>
      <form onSubmit={handleSafetyMonitorSubmit} className="form">
        <label>
          <span>Location</span>
          <input
            type="text"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            placeholder="例如 校园 / 商圈"
            required
          />
        </label>
        <label>
          <span>Safety Status</span>
          <select
            value={safetyStatus}
            onChange={(e) => setSafetyStatus(e.target.value)}
          >
            <option value="">请选择</option>
            <option value="ok">OK</option>
            <option value="warning">Warning</option>
            <option value="incident">Incident</option>
          </select>
        </label>
        <button type="submit" className="btn" disabled={loading}>
          {loading ? "Sending..." : "Send Safety Status"}
        </button>
      </form>
    </section>
  </section>

  {/* 右侧：交互记录（可滚动 + 可拖拽调高） */}
  <section className="interaction-record">
    <section className="card">
      <h2 className="card__title">交互记录</h2>

      <div
        className="history-scroll"
        style={{ height: `${historyHeight}px` }}
      >
        {history.length === 0 ? (
          <p className="muted">暂无记录，发送任一模块后将在此显示。</p>
        ) : (
          <ul className="history">
            {history.map((h) => (
              <li key={h.id}>
                <div className="history__line">
                  {h.traffic_condition && (
                    <span className="pill">{h.traffic_condition}</span>
                  )}
                  {h.incident_detected && (
                    <span className="tag">incident</span>
                  )}
                  <span className="loc">{h.location || h.area || "-"}</span>
                </div>
                <div className="history__meta">
                  <span>{h.at}</span>
                  {typeof h.vehicle_speed !== "undefined" && (
                    <span>{h.vehicle_speed} km/h</span>
                  )}
                  {typeof h.available_spots !== "undefined" && (
                    <span>{h.available_spots} spots</span>
                  )}
                  {h.alert_type && <span>⚠ {h.alert_type}</span>}
                  {h.safety_status && <span>🛡 {h.safety_status}</span>}
                  <span className={h.message === "received" ? "ok" : ""}>
                    {h.message}
                  </span>
                </div>
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* 拖拽条 */}
      <div
        className="drag-bar"
        title="拖动以调整交互记录高度"
        onMouseDown={handleMouseDown}
      />
    </section>
  </section>
</main>

  // 拖拽调整交互记录高度
  const handleMouseDown = (e) => {
    const startY = e.clientY;
    const startHeight = historyHeight;

    const onMove = (ev) => {
      const diff = ev.clientY - startY;
      setHistoryHeight((h) => Math.max(150, Math.min(600, startHeight + diff))); // 限制最小高度 150px，最大高度 600px
    };
    const onUp = () => {
      window.removeEventListener("mousemove", onMove);
      window.removeEventListener("mouseup", onUp);
    };
    window.addEventListener("mousemove", onMove);
    window.addEventListener("mouseup", onUp);
  };

  return (
    <div className="page">
      <header className="page__header">
        <h1>自动驾驶 × 城市管理控制面板</h1>
        <p className="subtitle">左侧四个模块发起任务 / 右侧查看多智能体交互记录</p>
      </header>

      <main className="page__main">
        {/* 左侧模块：四个任务表单（2x2） */}
        <section className="modules">
          {/* 发送任务 */}
          <section className="card">
            <h2 className="card__title">发送任务</h2>
            <form onSubmit={handleSubmit} className="form">
              <div className="form__row">
                <label>
                  <span>Location</span>
                  <input
                    type="text"
                    value={location}
                    onChange={(e) => setLocation(e.target.value)}
                    placeholder="如：Main St."
                    required
                  />
                </label>
                <label>
                  <span>Traffic Condition</span>
                  <select
                    value={trafficCondition}
                    onChange={(e) => setTrafficCondition(e.target.value)}
                  >
                    <option value="normal">Normal</option>
                    <option value="heavy">Heavy</option>
                  </select>
                </label>
              </div>

              <div className="form__row">
                <label className="checkbox">
                  <input
                    type="checkbox"
                    checked={incidentDetected}
                    onChange={(e) => setIncidentDetected(e.target.checked)}
                  />
                  <span>Incident Detected</span>
                </label>

                <label>
                  <span>Vehicle Speed (km/h)</span>
                  <input
                    type="number"
                    min="0"
                    value={vehicleSpeed}
                    onChange={(e) => setVehicleSpeed(e.target.value)}
                    placeholder="范围：0-200整数"
                    required
                  />
                </label>
              </div>

              <button type="submit" className="btn" disabled={loading || !isValid}>
                {loading ? "Sending..." : "Send"}
              </button>
            </form>

            {resp && (
              <div className={`alert ${resp.ok ? "ok" : "err"}`}>
                <strong>Response:</strong> {resp.message}
              </div>
            )}
          </section>

          {/* 其它模块：天气告警、停车位更新、安全巡检 */}
          {/* 模块代码省略，保持一致的结构 */}
        </section>

        {/* 右侧：交互记录（可滚动 + 可拖拽调高） */}
        <section className="interaction-record">
          <section className="card">
            <h2 className="card__title">交互记录</h2>
            <div className="history-scroll" style={{ height: `${historyHeight}px` }}>
              {history.length === 0 ? (
                <p className="muted">暂无记录，发送任一模块后将在此显示。</p>
              ) : (
                <ul className="history">
                  {history.map((h) => (
                    <li key={h.id}>
                      <div className="history__line">
                        <span className="pill">{h.traffic_condition}</span>
                        {h.incident_detected && <span className="tag">incident</span>}
                        <span className="loc">{h.location || h.area || "-"}</span>
                      </div>
                      <div className="history__meta">
                        <span>{h.at}</span>
                        {typeof h.vehicle_speed !== "undefined" && <span>{h.vehicle_speed} km/h</span>}
                        {typeof h.available_spots !== "undefined" && <span>{h.available_spots} spots</span>}
                        {h.alert_type && <span>⚠ {h.alert_type}</span>}
                        {h.safety_status && <span>🛡 {h.safety_status}</span>}
                        <span className={h.message === "received" ? "ok" : ""}>
                          {h.message}
                        </span>
                      </div>
                    </li>
                  ))}
                </ul>
              )}
            </div>
            <div className="drag-bar" title="拖动调整高度" onMouseDown={handleMouseDown} />
          </section>
        </section>
      </main>
    </div>
  );
}

// 获取随机ID
function cryptoRandomId() {
  if (window.crypto?.randomUUID) return window.crypto.randomUUID();
  return "id-" + Math.random().toString(36).slice(2);
}
