import React from 'react';
import './AgentSelector.css';

const AgentSelector = ({ 
  agents, 
  selectedAgents, 
  isMultiAgentMode, 
  onToggleAgentSelection,
  onModeChange 
}) => {
  const toggleAgentSelection = (agent) => {
    if (isMultiAgentMode) {
      // Â§öÊô∫ËÉΩ‰ΩìÊ®°ÂºèÔºöÂèØ‰ª•Â§öÈÄâ
      const isSelected = selectedAgents.some(a => a.id === agent.id);
      if (isSelected) {
        onToggleAgentSelection(selectedAgents.filter(a => a.id !== agent.id));
      } else {
        onToggleAgentSelection([...selectedAgents, agent]);
      }
    } else {
      // ÂçïÊô∫ËÉΩ‰ΩìÊ®°ÂºèÔºöÂè™ËÉΩÈÄâÊã©‰∏Ä‰∏™
      onToggleAgentSelection([agent]);
    }
  };

  return (
    <div className="agent-selector">
      <div className="selector-header">
        <h3 className="selector-title">
          {isMultiAgentMode ? 'ÈÄâÊã©Âçè‰ΩúÊô∫ËÉΩ‰Ωì' : 'ÈÄâÊã©ÊâßË°åÊô∫ËÉΩ‰Ωì'}
        </h3>

        {/* Ê®°ÂºèÂàáÊç¢ */}
        <div className="mode-switcher">
          <button 
            className={`mode-btn ${!isMultiAgentMode ? 'active' : ''}`}
            onClick={() => onModeChange(false)}
          >
            <span className="mode-icon">ü§ñ</span>
            <span className="mode-label">ÂçïÊô∫ËÉΩ‰Ωì</span>
          </button>
          <button 
            className={`mode-btn ${isMultiAgentMode ? 'active' : ''}`}
            onClick={() => onModeChange(true)}
          >
            <span className="mode-icon">üë•</span>
            <span className="mode-label">Â§öÊô∫ËÉΩ‰Ωì</span>
          </button>
        </div>
      </div>

      <div className="agent-grid">
        {agents.map(agent => (
          <div 
            key={agent.id}
            className={`agent-card ${selectedAgents.some(a => a.id === agent.id) ? 'selected' : ''}`}
            onClick={() => toggleAgentSelection(agent)}
          >
            <div className={`agent-icon ${agent.color}-icon`}>
              {agent.icon && React.createElement(agent.icon)}
            </div>
            <div className="agent-info">
              <h4 className="agent-name">{agent.name}</h4>
              <p className="agent-role">{agent.role}</p>
            </div>
            <div className="selection-indicator">
              {selectedAgents.some(a => a.id === agent.id) ? '‚úì' : ''}
            </div>
          </div>
        ))}
      </div>

      <div className="selection-summary">
        <div className="summary-text">
          Â∑≤ÈÄâÊã© {selectedAgents.length} ‰∏™Êô∫ËÉΩ‰Ωì
        </div>
        {selectedAgents.length > 0 && (
          <div className="selected-list">
            {selectedAgents.map(agent => (
              <span key={agent.id} className="selected-tag">
                {agent.name}
              </span>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default AgentSelector;
