import { io } from 'socket.io-client';

class SimpleWebSocketService {
  constructor() {
    this.socket = null;
    this.isConnected = false;
    this.onConnectCallback = null;
    this.onDisconnectCallback = null;
    this.onMessageCallback = null;
    this.onErrorCallback = null;
