    this.connectionStats = {
      totalConnections: 0,
      successfulConnections: 0,
      failedConnections: 0,
      lastConnectionTime: null,
      lastError: null
    };
  }

  generateUserId() {
    if (typeof window === 'undefined') {
      return `user_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
    }

    let userId = localStorage.getItem('websocket_user_id');
    if (!userId) {
      userId = `user_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
      localStorage.setItem('websocket_user_id', userId);
    }
    return userId;
  }

  connect(url) {
    console.log('üîå ËøûÊé•Âà∞:', url);

    // ÂÖàÊñ≠ÂºÄÁé∞ÊúâËøûÊé•
    if (this.socket) {
      this.socket.disconnect();
      this.socket = null;
    }

    try {
      // Á°Æ‰øùURLÊ†ºÂºèÊ≠£Á°Æ
      const baseUrl = url.replace(/\/$/, ''); // ÁßªÈô§Êú´Â∞æÊñúÊù†
      const connectionUrl = `${baseUrl}?user_id=${this.userId}`;

      // ‰ΩøÁî®Socket.IOËøûÊé•ÔºåÂ¢ûÂä†Êõ¥Â§öÈÖçÁΩÆÈÄâÈ°π
      this.socket = io(connectionUrl, {
        transports: ['websocket', 'polling'],
        timeout: 30000,
        forceNew: true,
        reconnection: true,
        reconnectionAttempts: 10,
        reconnectionDelay: 2000,
        reconnectionDelayMax: 10000,
        maxReconnectionAttempts: 10,
        autoConnect: true,
        upgrade: true,
        rememberUpgrade: true
      });

      this.socket.on('connect', () => {
        console.log('‚úÖ WebSocketËøûÊé•ÊàêÂäü');
        this.isConnected = true;
        this.lastError = null;
        this.connectionStats.successfulConnections++;
        this.connectionStats.lastConnectionTime = new Date().toISOString();
        if (this.onConnectCallback) {
          this.onConnectCallback();
        }
      });

      this.socket.on('disconnect', (reason) => {
        console.log('‚ùå WebSocketÊñ≠ÂºÄËøûÊé•:', reason);
        this.isConnected = false;
        if (this.onDisconnectCallback) {
          this.onDisconnectCallback(reason);
        }
      });

      this.socket.on('connect_error', (error) => {
        console.error('‚ùå WebSocketËøûÊé•ÈîôËØØ:', error);
        this.isConnected = false;
        this.lastError = error;
        this.connectionStats.failedConnections++;
        this.connectionStats.lastError = error.message || error.toString();
        if (this.onErrorCallback) {
          this.onErrorCallback(error);
        }
      });

      this.socket.on('reconnect', (attemptNumber) => {
        console.log('üîÑ WebSocketÈáçËøûÊàêÂäüÔºåÂ∞ùËØïÊ¨°Êï∞:', attemptNumber);
        this.isConnected = true;
        if (this.onConnectCallback) {
          this.onConnectCallback();
        }
      });

      this.socket.on('reconnect_error', (error) => {
        console.error('‚ùå WebSocketÈáçËøûÂ§±Ë¥•:', error);
        this.isConnected = false;
      });

      // ÁõëÂê¨ËøûÊé•ÊàêÂäü‰∫ã‰ª∂
      this.socket.on('connection_successful', (data) => {
        console.log('üéâ Êî∂Âà∞ËøûÊé•ÊàêÂäüÁ°ÆËÆ§:', data);
        this.isConnected = true;
        if (this.onConnectCallback) {
          this.onConnectCallback();
        }
      });

      // ÁõëÂê¨ÊúçÂä°Âô®ÁªüËÆ°
      this.socket.on('server_stats', (stats) => {
        console.log('üìä ÊúçÂä°Âô®ÁªüËÆ°:', stats);
      });

      // ÁõëÂê¨ÊâÄÊúâÊ∂àÊÅØ
      this.socket.onAny((eventName, ...args) => {
        console.log('üì® Êî∂Âà∞Ê∂àÊÅØ:', eventName, args);
        if (this.onMessageCallback) {
          const message = args[0] || {};
          this.onMessageCallback(message);
        }
      });

      // ÁâπÂà´ÁõëÂê¨ÂπøÊí≠Ê∂àÊÅØ
      this.socket.on('broadcast', (data) => {
        console.log('üì° Êî∂Âà∞ÂπøÊí≠Ê∂àÊÅØ:', data);
        if (this.onMessageCallback) {
          this.onMessageCallback(data);
        }
      });

    } catch (error) {
      console.error('‚ùå WebSocketÂàùÂßãÂåñÂ§±Ë¥•:', error);
      if (this.onErrorCallback) {
        this.onErrorCallback(error);
      }
    }
  }

  send(event, data) {
    if (!this.isConnected || !this.socket) {
      console.error('‚ùå WebSocketÊú™ËøûÊé•');
      return;
    }

    console.log('üì§ ÂèëÈÄÅÊ∂àÊÅØ:', { event, data });

    try {
      this.socket.emit(event, data);
    } catch (error) {
      console.error('‚ùå ÂèëÈÄÅÊ∂àÊÅØÂ§±Ë¥•:', error);
      if (this.onErrorCallback) {
        this.onErrorCallback(error);
      }
    }
