        console.error(`Unknown event: ${event}`);
    }
  }

  disconnect() {
    this.isConnected = false;
    if (this.socket) {
      this.socket.disconnect();
      this.socket = null;
    }
    if (this.onDisconnectCallback) {
      this.onDisconnectCallback();
    }
  }

  reconnect() {
    console.log('üîÑ ÊâãÂä®ÈáçËøûWebSocket...');
    if (this.socket) {
      this.socket.disconnect();
    }
    // ÈáçÊñ∞ËøûÊé•
    setTimeout(() => {
      this.connect('http://localhost:8008');
    }, 1000);
  }

  getConnectionStats() {
    return {
      ...this.connectionStats,
      connected: this.isConnected,
      userId: this.userId,
      socketId: this.socket?.id || null,
      lastError: this.lastError
    };
  }

  getConnectionStatus() {
    if (!this.socket) {
      return { status: 'disconnected', message: 'Êú™ÂàùÂßãÂåñËøûÊé•' };
    }

    if (this.isConnected) {
      return { status: 'connected', message: 'ËøûÊé•Ê≠£Â∏∏' };
    }

    if (this.lastError) {
      return { 
        status: 'error', 
        message: `ËøûÊé•ÈîôËØØ: ${this.lastError.message || this.lastError}` 
      };
