    return { status: 'connecting', message: 'Ê≠£Âú®ËøûÊé•...' };
  }

  // Ê∑ªÂä†ÊâãÂä®ÈáçËøûÊñπÊ≥ï
  reconnect() {
    if (this.socket) {
      console.log('üîÑ ÊâãÂä®ÈáçËøûWebSocket');
      this.socket.disconnect();
      this.socket.connect();
    }
  }
}

const instance = new SimpleWebSocketService();
export default instance;
