// frontend/src/components/InteractionHistory.jsx
import React, { useState } from 'react';
import './InteractionHistory.css';
import WebSocketService from './WebSocketService';

const InteractionHistory = ({ events = [], onClear, onGenerateReport, readyState, connectionStats }) => {
  const [selectedSentEvent, setSelectedSentEvent] = useState(null);
  const [expandedEvents, setExpandedEvents] = useState(new Set());

  const getStatusPill = () => {
    switch (readyState) {
      case 0: // CONNECTING
        return <span className="pill pill-status-connecting">üîÑ ËøûÊé•‰∏≠ / Connecting...</span>;
      case 1: // OPEN
        return <span className="pill pill-status-open">‚úÖ Â∑≤ËøûÊé• / Connected</span>;
      case 2: // CLOSING
        return <span className="pill pill-status-closing">‚è≥ Êñ≠ÂºÄ‰∏≠ / Disconnecting...</span>;
      case 3: // CLOSED
        return <span className="pill pill-status-closed">‚ùå ÊúçÂä°‰∏çÂèØÁî® / Service Unavailable</span>;
      default:
        return <span className="pill pill-status-closed">‚ùå ÊúçÂä°‰∏çÂèØÁî® / Service Unavailable</span>;
    }
  };

  const handleReconnect = () => {
    WebSocketService.reconnect();
  };

  // Ëé∑Âèñ‰∫ã‰ª∂ÊëòË¶Å
  const getEventSummary = (event) => {
    switch (event.type) {
      case 'main_task_result':
        return `${event.payload.agent}: ${event.payload.result.substring(0, 50)}...`;
      case 'coordination_result':
        return `ÂçèÂêåÂÆåÊàê: ${event.payload.total_agents}‰∏™Êô∫ËÉΩ‰ΩìÂìçÂ∫î`;
      case 'workflow_complete':
        return 'Â∑•‰ΩúÊµÅÂÆåÊàê';
      case 'agent_response':
        return `${event.payload.agent}: ${event.payload.result.substring(0, 50)}...`;
      case 'agent_message':
        return event.payload.substring(0, 50) + '...';
      case 'analysis_report':
        return 'ÂàÜÊûêÊä•ÂëäÂ∑≤ÁîüÊàê';
      case 'simulation_log':
        return event.payload.details ? event.payload.details.substring(0, 50) + '...' : 'Ê®°ÊãüÊó•Âøó';
      default:
        return event.title || event.type;
    }
  };

  const getEventIcon = (type) => {
    const icons = {
      'workflow_start': 'üöÄ',
      'main_task_result': 'üëë', // Master Agent ‰ΩøÁî®ÁöáÂÜ†ÂõæÊ†á
      'coordination_start': 'ü§ù',
      'coordination_result': 'üéØ',
      'workflow_complete': 'üèÅ',
      'agent_message': 'ü§ñ',
      'agent_response': 'üí¨',
      'analysis_report': 'üìä',
      'traffic_incident': 'üöó',
      'weather_alert': 'üåßÔ∏è',
      'parking_update': 'üÖøÔ∏è',
      'safety_inspection': 'üõ°Ô∏è',
      'autonomous_driving': 'üöô',
      'traffic_monitor': 'üö¶',
      'simulation_log': 'üìù',
      'error': '‚ùå',
      'ping': 'üì°',
      'default': 'üìù'
    };
    return icons[type] || icons['default'];
  };

  const getEventTypeLabel = (type) => {
    const labels = {
      'workflow_start': 'Â∑•‰ΩúÊµÅÂêØÂä® / Workflow Start',
      'main_task_result': '‰∏ªÊô∫ËÉΩ‰ΩìÂçèË∞É / Master Agent Coordination',
      'coordination_start': 'Â≠êÊô∫ËÉΩ‰ΩìÂçèË∞É / Sub Agent Coordination',
      'coordination_result': 'Â≠êÊô∫ËÉΩ‰ΩìÂçèÂêå / Sub Agent Collaboration',
      'workflow_complete': 'Â∑•‰ΩúÊµÅÂÆåÊàê / Workflow Complete',
      'agent_message': 'Êô∫ËÉΩ‰ΩìÊ∂àÊÅØ / Agent Message',
      'agent_response': 'Êô∫ËÉΩ‰ΩìÂìçÂ∫î / Agent Response',
      'analysis_report': 'ÂàÜÊûêÊä•Âëä / Analysis Report',
      'traffic_incident': '‰∫§ÈÄö‰∫ã‰ª∂ / Traffic Incident',
      'weather_alert': 'Â§©Ê∞îË≠¶Êä• / Weather Alert',
      'parking_update': 'ÂÅúËΩ¶Êõ¥Êñ∞ / Parking Update',
      'safety_inspection': 'ÂÆâÂÖ®Ê£ÄÊü• / Safety Inspection',
      'autonomous_driving': 'Ëá™Âä®È©æÈ©∂ / Autonomous Driving',
      'traffic_monitor': '‰∫§ÈÄöÁõëÊéß / Traffic Monitor',
      'simulation_log': 'Ê®°ÊãüÊó•Âøó / Simulation Log',
      'error': 'ÈîôËØØ / Error',
      'ping': 'ÂøÉË∑≥ / Ping',
      'default': 'Êú™Áü•‰∫ã‰ª∂ / Unknown Event'
    };
    return labels[type] || labels['default'];
  };

  const renderEvent = (event, index) => {
    if (!event) {
      return <p>No event data</p>;
    }

    const { type, payload, title, timestamp } = event;
    const time = timestamp ? new Date(timestamp).toLocaleTimeString() : 'No timestamp';
    const icon = getEventIcon(type);
    const typeLabel = getEventTypeLabel(type);

    const eventCard = (
      <div className={`event-card event-${type}`} key={`${type}-${timestamp || index}-${index}`}>
        <div className="event-header">
          <div className="event-icon">{icon}</div>
          <div className="event-info">
            <h4 className="event-title">{title || typeLabel}</h4>
            <span className="event-time">{time}</span>
          </div>
          <div className="event-actions">
            <button 
              className="action-btn view-btn" 
              onClick={() => viewEventDetails(event)}
              title="Êü•ÁúãËØ¶ÊÉÖ / View Details"
            >
              üëÅÔ∏è
            </button>
            {type === 'agent_response' && (
              <button 
                className="action-btn replay-btn" 
                onClick={() => replayEvent(event)}
                title="ÈáçÊîæ‰∫ã‰ª∂ / Replay Event"
              >
                üîÑ
              </button>
            )}
            {type === 'error' && (
              <button 
                className="action-btn retry-btn" 
                onClick={() => retryEvent(event)}
                title="ÈáçËØï / Retry"
              >
                ‚ö°
              </button>
            )}
          </div>
        </div>
        <div className="event-content">
          {renderEventContent(type, payload)}
        </div>
      </div>
    );

    return eventCard;
  };

  const renderEventContent = (type, payload) => {
    switch (type) {
      case 'workflow_start':
        return (
          <div className="event-payload">
            <p className="payload-text">{payload}</p>
          </div>
        );
      case 'main_task_result':
        return (
          <div className="event-payload">
            <p className="payload-text"><strong>{payload.agent}:</strong> {payload.result}</p>
          </div>
        );
      case 'coordination_start':
        return (
          <div className="event-payload">
            <p className="payload-text">{payload}</p>
          </div>
        );
      case 'coordination_result':
        return (
          <div className="event-payload">
            <p className="payload-text"><strong>ÂçèÂêåÂÆåÊàê:</strong> {payload.total_agents}‰∏™Êô∫ËÉΩ‰ΩìÂìçÂ∫î</p>
            <div className="responses-summary">
              {payload.responses.map((response, index) => (
                <div key={index} className="response-summary">
                  <span className="response-agent">{response.agent}:</span>
                  <span className="response-text">{response.result.substring(0, 100)}...</span>
                </div>
              ))}
            </div>
          </div>
        );
      case 'workflow_complete':
        return (
          <div className="event-payload">
            <p className="payload-text">{payload}</p>
          </div>
        );
      case 'agent_message':
        return (
          <div className="event-payload">
            <p className="payload-text">{payload}</p>
          </div>
        );
      case 'agent_response':
        return (
          <div className="event-payload">
            <p className="payload-text"><strong>{payload.agent}:</strong> {payload.result}</p>
          </div>
        );
      case 'analysis_report':
        return (
          <div className="event-payload">
            <p className="payload-text">üìä ÂàÜÊûêÊä•ÂëäÂ∑≤ÁîüÊàê</p>
          </div>
        );
      case 'traffic_incident':
        return (
          <div className="event-payload">
            <p className="payload-text"><strong>‰ΩçÁΩÆ:</strong> {payload.location} - {payload.description}</p>
          </div>
        );
      case 'weather_alert':
        return (
          <div className="event-payload">
            <p className="payload-text"><strong>Âå∫Âüü:</strong> {payload.area} - <strong>Á±ªÂûã:</strong> {payload.alert_type} - <strong>‰∏•ÈáçÁ®ãÂ∫¶:</strong> {payload.severity}</p>
          </div>
        );
      case 'parking_update':
        return (
          <div className="event-payload">
            <p className="payload-text"><strong>‰ΩçÁΩÆ:</strong> {payload.location} - <strong>ÂèØÁî®ËΩ¶‰Ωç:</strong> {payload.available_spots}</p>
          </div>
        );
      case 'safety_inspection':
        return (
          <div className="event-payload">
            <p className="payload-text"><strong>‰ΩçÁΩÆ:</strong> {payload.location} - <strong>ÂÆâÂÖ®Áä∂ÊÄÅ:</strong> {payload.safety_status} - <strong>ÈúÄË¶Å‰∫∫Â∑•Âπ≤È¢Ñ:</strong> {payload.require_human_intervention ? 'ÊòØ' : 'Âê¶'}</p>
          </div>
        );
      case 'autonomous_driving':
        return (
          <div className="event-payload">
            <p className="payload-text"><strong>Ëµ∑ÁÇπ:</strong> {payload.start_location} - <strong>ÁªàÁÇπ:</strong> {payload.end_location} - <strong>‰πòÂÆ¢Êï∞:</strong> {payload.passengers}</p>
          </div>
        );
      case 'traffic_monitor':
        return (
          <div className="event-payload">
            <p className="payload-text"><strong>‰∫§ÈÄöÁõëÊéßÊï∞ÊçÆ:</strong> {JSON.stringify(payload)}</p>
          </div>
        );
      case 'simulation_log':
        return (
          <div className="event-payload">
            <p className="payload-text">{payload.details || payload}</p>
          </div>
        );
      case 'error':
        return (
          <div className="event-payload">
            <p className="payload-error">{payload}</p>
          </div>
        );
      case 'ping':
        return null; // Don't display ping messages
      default:
        return (
          <div className="event-payload">
            <p className="payload-text">{JSON.stringify(payload)}</p>
          </div>
        );
    }
  };

  const viewEventDetails = (event) => {
    alert(`‰∫ã‰ª∂ËØ¶ÊÉÖ / Event Details\n\nÁ±ªÂûã / Type: ${getEventTypeLabel(event.type)}\nÊó∂Èó¥ / Time: ${event.timestamp}\n\nËØ¶ÁªÜ‰ø°ÊÅØÂ∞ÜÂú®Ê®°ÊÄÅÊ°Ü‰∏≠ÊòæÁ§∫ / Detailed information will be shown in a modal.`);
  };

  const replayEvent = (event) => {
    if (confirm(`Á°ÆÂÆöË¶ÅÈáçÊîæÊ≠§‰∫ã‰ª∂ÂêóÔºü / Are you sure you want to replay this event?`)) {
      // ËøôÈáåÂèØ‰ª•Ê∑ªÂä†ÈáçÊîæÈÄªËæë
      alert('‰∫ã‰ª∂Â∑≤ÈáçÊîæ / Event replayed successfully!');
    }
  };

  const retryEvent = (event) => {
    if (confirm(`Á°ÆÂÆöË¶ÅÈáçËØïÊ≠§‰∫ã‰ª∂ÂêóÔºü / Are you sure you want to retry this event?`)) {
      // ËøôÈáåÂèØ‰ª•Ê∑ªÂä†ÈáçËØïÈÄªËæë
      alert('‰∫ã‰ª∂Â∑≤ÈáçËØï / Event retried successfully!');
    }
  };

  const getEventStats = () => {
    const total = events.length;
    const byType = events.reduce((acc, event) => {
      acc[event.type] = (acc[event.type] || 0) + 1;
      return acc;
    }, {});
    return { total, byType };
  };

  // Ëé∑ÂèñÂèëÈÄÅÁöÑ‰∫ã‰ª∂ÔºàÁî®Êà∑‰∏ªÂä®ÂèëÈÄÅÁöÑ‰∫ã‰ª∂Ôºâ
  const getSentEvents = () => {
    const sentEventTypes = ['weather_alert', 'parking_update', 'safety_inspection', 'autonomous_driving', 'traffic_monitor'];
    return events.filter(event => sentEventTypes.includes(event.type));
  };

  // Ëé∑ÂèñÂèëÈÄÅ‰∫ã‰ª∂Êï∞Èáè
  const getSentEventsCount = () => {
    return getSentEvents().length;
  };

  // Ëé∑ÂèñÂèëÈÄÅ‰∫ã‰ª∂ÊëòË¶Å
  const getSentEventSummary = (event) => {
    if (!event || !event.payload) {
      return event?.title || event?.type || 'Êú™Áü•‰∫ã‰ª∂';
    }

    switch (event.type) {
      case 'weather_alert':
        return `Âå∫Âüü: ${event.payload.area || 'Êú™Áü•'} - Á±ªÂûã: ${event.payload.alert_type || 'Êú™Áü•'}`;
      case 'parking_update':
        return `‰ΩçÁΩÆ: ${event.payload.location || 'Êú™Áü•'} - ÂèØÁî®ËΩ¶‰Ωç: ${event.payload.available_spots || 'Êú™Áü•'}`;
      case 'safety_inspection':
        return `‰ΩçÁΩÆ: ${event.payload.location || 'Êú™Áü•'} - Áä∂ÊÄÅ: ${event.payload.safety_status || 'Êú™Áü•'}`;
      case 'autonomous_driving':
        return `Ëµ∑ÁÇπ: ${event.payload.start_location || 'Êú™Áü•'} - ÁªàÁÇπ: ${event.payload.end_location || 'Êú™Áü•'}`;
      case 'traffic_monitor':
        return `‰∫§ÈÄöÁõëÊéßÊï∞ÊçÆ`;
      default:
        return event.title || event.type;
    }
  };

  // Ëé∑Âèñ‰∏éÂèëÈÄÅ‰∫ã‰ª∂Áõ∏ÂÖ≥ÁöÑÂìçÂ∫î‰∫ã‰ª∂
  const getRelatedEvents = (sentEvent) => {
    if (!sentEvent || !sentEvent.timestamp) {
      return [];
    }

    const sentTime = new Date(sentEvent.timestamp);
    const timeWindow = 10 * 60 * 1000; // Â¢ûÂä†Âà∞10ÂàÜÈíüÊó∂Èó¥Á™óÂè£

    // ÂÆö‰πâÊô∫ËÉΩ‰ΩìÂìçÂ∫î‰∫ã‰ª∂Á±ªÂûã
    const responseEventTypes = [
      'workflow_start',
      'main_task_result', 
      'coordination_start',
      'coordination_result',
      'workflow_complete',
      'agent_message',
      'agent_response',
      'analysis_report',
      'simulation_log'
    ];

    return events.filter(event => {
      if (!event || !event.timestamp) return false;

      const eventTime = new Date(event.timestamp);
      const timeDiff = Math.abs(eventTime.getTime() - sentTime.getTime());

      // Âú®Êó∂Èó¥Á™óÂè£ÂÜÖÔºåÊòØÂìçÂ∫î‰∫ã‰ª∂Á±ªÂûãÔºå‰∏î‰∏çÊòØÂèëÈÄÅ‰∫ã‰ª∂Êú¨Ë∫´
      return timeDiff <= timeWindow && 
             responseEventTypes.includes(event.type) && 
             event.type !== sentEvent.type;
    }).sort((a, b) => new Date(a.timestamp) - new Date(b.timestamp));
  };

  const stats = getEventStats();

  return (
    <div className="interaction-record card">
      <div className="history-header">
        <h2 className="card__title">üìä ‰∫§‰∫íÂéÜÂè≤ / Interaction History</h2>
        <div className="header-controls">
          {getStatusPill()}
          {readyState !== 1 && (
            <button onClick={handleReconnect} className="reconnect-btn">
              üîÑ ÈáçËøû / Reconnect
            </button>
          )}
        </div>
      </div>

      {/* ËøûÊé•Áä∂ÊÄÅ‰ø°ÊÅØ */}
      <div className="connection-info">
        <div className="connection-details">
          <span className="connection-item">
            <strong>Ê∂àÊÅØÊï∞ / Messages:</strong> {events.length}
          </span>
          <span className="connection-item">
            <strong>ÂêéÁ´Ø / Backend:</strong> http://localhost:8008
          </span>
          {connectionStats && (
            <>
              <span className="connection-item">
                <strong>Áî®Êà∑ID / User ID:</strong> {connectionStats.userId}
              </span>
              <span className="connection-item">
                <strong>Ê¥ªË∑ÉËøûÊé• / Active Connections:</strong> {connectionStats.activeConnections}/{connectionStats.totalConnections}
              </span>
            </>
          )}
        </div>
      </div>

      <div className="history-controls">
        <div className="action-buttons">
          <button onClick={onGenerateReport} className="generate-btn">
            üìÑ ÁîüÊàêÊä•Âëä / Generate Report
          </button>
          <button onClick={onClear} className="clear-btn">
            üóëÔ∏è Ê∏ÖÁ©∫ÂéÜÂè≤ / Clear History
          </button>
        </div>
      </div>

      <div className="history-layout">
        {/* Â∑¶‰æßÔºöÂèëÈÄÅÁöÑ‰∫ã‰ª∂ËØ¶ÊÉÖ */}
        <div className="sent-events-sidebar">
          <div className="sidebar-header">
            <h3>üì§ ÂèëÈÄÅÁöÑ‰∫ã‰ª∂ / Sent Events</h3>
            <span className="event-count">{getSentEventsCount()} ‰∏™‰∫ã‰ª∂</span>
          </div>
          <div className="sent-events-list">
            {getSentEvents().length === 0 ? (
              <div className="empty-state">
                <div className="empty-icon">üì≠</div>
                <p className="empty-text">ÊöÇÊó†ÂèëÈÄÅ‰∫ã‰ª∂ / No sent events yet</p>
                <p className="empty-subtext">ÂèëÈÄÅ‰ªªÂä°‰ª•Êü•Áúã‰∫ã‰ª∂ËØ¶ÊÉÖ / Send tasks to view event details</p>
              </div>
            ) : (
              getSentEvents().map((event, index) => (
                <div 
                  key={index} 
                  className={`sent-event-item ${selectedSentEvent === index ? 'selected' : ''}`}
                  onClick={() => setSelectedSentEvent(index)}
                >
                  <div className="event-header">
                    <span className="event-icon">{getEventIcon(event.type)}</span>
                    <div className="event-info">
                      <div className="event-title">{getEventTypeLabel(event.type)}</div>
                      <div className="event-meta">
                        <span className="event-time">{event.timestamp ? new Date(event.timestamp).toLocaleTimeString() : 'Êú™Áü•Êó∂Èó¥'}</span>
                      </div>
                    </div>
                  </div>
                  <div className="event-summary">
                    {getSentEventSummary(event)}
                  </div>
                </div>
              ))
            )}
          </div>
        </div>

        {/* Âè≥‰æßÔºö‰∫§‰∫íËÆ∞ÂΩïÂíåÊô∫ËÉΩ‰ΩìÂìçÂ∫î */}
        <div className="interactions-detail">
          {selectedSentEvent !== null ? (
            <div className="detail-view">
              <div className="detail-header">
                <h3>ü§ñ Êô∫ËÉΩ‰Ωì‰∫§‰∫íËÆ∞ÂΩï / Agent Interaction Records</h3>
                <button 
                  className="close-detail"
                  onClick={() => setSelectedSentEvent(null)}
                >
                  ‚úï
                </button>
              </div>
              <div className="detail-content">
                {(() => {
                  const sentEvent = getSentEvents()[selectedSentEvent];
                  if (!sentEvent) return null;

                  // Ëé∑Âèñ‰∏éËøô‰∏™ÂèëÈÄÅ‰∫ã‰ª∂Áõ∏ÂÖ≥ÁöÑÊâÄÊúâÂìçÂ∫î‰∫ã‰ª∂
                  const relatedEvents = getRelatedEvents(sentEvent);

                  return (
                    <div className="interaction-flow">
                      {/* ÊòæÁ§∫ÂèëÈÄÅÁöÑ‰∫ã‰ª∂ËØ¶ÊÉÖ */}
                      <div className="sent-event-detail">
                        <div className="detail-section-header">
                          <span className="section-icon">üì§</span>
                          <span className="section-title">ÂèëÈÄÅÁöÑ‰∫ã‰ª∂ËØ¶ÊÉÖ / Sent Event Details</span>
                        </div>
                        <div className="event-detail-content">
                          <div className="detail-item">
                            <strong>‰∫ã‰ª∂Á±ªÂûã / Event Type:</strong> {getEventTypeLabel(sentEvent.type)}
                          </div>
                          <div className="detail-item">
                            <strong>ÂèëÈÄÅÊó∂Èó¥ / Sent Time:</strong> {new Date(sentEvent.timestamp).toLocaleString()}
                          </div>
                          <div className="detail-item">
                            <strong>‰∫ã‰ª∂ËØ¶ÊÉÖ / Event Details:</strong>
                            <div className="event-payload-detail">
                              {renderEventContent(sentEvent.type, sentEvent.payload)}
                            </div>
                          </div>
                        </div>
                      </div>

                      {/* ÊòæÁ§∫Êô∫ËÉΩ‰ΩìÂìçÂ∫îÊµÅÁ®ã */}
                      <div className="agent-responses">
                        <div className="detail-section-header">
                          <span className="section-icon">ü§ñ</span>
                          <span className="section-title">Êô∫ËÉΩ‰ΩìÂìçÂ∫îÊµÅÁ®ã / Agent Response Flow</span>
                        </div>
                        <div className="response-timeline">
                          {relatedEvents.length === 0 ? (
                            <div className="no-responses">
                              <div className="no-responses-icon">‚è≥</div>
                              <p>Á≠âÂæÖÊô∫ËÉΩ‰ΩìÂìçÂ∫î / Waiting for agent responses...</p>
                            </div>
                          ) : (
                            relatedEvents.map((event, index) => (
                              <div key={index} className="response-item">
                                <div className="response-header">
                                  <span className="response-icon">{getEventIcon(event.type)}</span>
                                  <span className="response-type">{getEventTypeLabel(event.type)}</span>
                                  <span className="response-time">{new Date(event.timestamp).toLocaleTimeString()}</span>
                                  <button 
                                    className={`expand-btn ${expandedEvents.has(`response_${selectedSentEvent}_${index}`) ? 'expanded' : ''}`}
                                    onClick={() => {
                                      const eventKey = `response_${selectedSentEvent}_${index}`;
                                      const newExpanded = new Set(expandedEvents);
                                      if (newExpanded.has(eventKey)) {
                                        newExpanded.delete(eventKey);
                                      } else {
                                        newExpanded.add(eventKey);
                                      }
                                      setExpandedEvents(newExpanded);
                                    }}
