import React, { createContext, useContext, useReducer, useCallback } from 'react';
import useWebSocket from '../hooks/useWebSocket';

const EventContext = createContext();

const initialState = {
  events: [],
  traffic: {},
  weather: {},
  parking: {},
  safety: {},
};

const eventReducer = (state, action) => {
  switch (action.type) {
    case 'ADD_EVENT':
      return { ...state, events: [...state.events, action.payload] };
    case 'SET_TRAFFIC':
      return { ...state, traffic: action.payload };
    case 'SET_WEATHER':
      return { ...state, weather: action.payload };
    case 'SET_PARKING':
      return { ...state, parking: action.payload };
    case 'SET_SAFETY':
      return { ...state, safety: action.payload };
    default:
      return state;
  }
};

const wsUrl = 'ws://localhost:8000/ws';

export const EventProvider = ({ children }) => {
  const [state, dispatch] = useReducer(eventReducer, initialState);

  const onMessage = useCallback((message) => {
    const { type, payload, title } = message;

    let newEvent;

    switch (type) {
      case 'traffic_incident':
        newEvent = {
          id: `traffic-${Date.now()}`,
          at: new Date().toLocaleTimeString(),
          location: payload.location,
          event_type: type,
          severity: payload.severity,
          message: title,
          description: payload.description,
        };
        break;
      case 'autonomous_driving':
        newEvent = {
          id: payload.timestamp + Math.random(),
          at: new Date(payload.timestamp).toLocaleTimeString(),
          location: payload.road,
          event_type: payload.event_type,
          severity: payload.severity,
          vehicles: payload.vehicles,
          lanes_blocked: payload.lanes_blocked,
          message: title,
        };
        break;
      default:
        console.warn('Received unknown event type:', type);
        return;
    }

    dispatch({ type: 'ADD_EVENT', payload: newEvent });
  }, [dispatch]);

  const { sendMessage, readyState } = useWebSocket(wsUrl, onMessage);

  return (
    <EventContext.Provider value={{ state, dispatch, sendMessage, readyState }}>
      {children}
    </EventContext.Provider>
  );
};

export const useEventContext = () => useContext(EventContext);
