import React, { useState, useMemo, useEffect } from "react";
import axios from "axios";
import "./App.css";

// API 配置
const API_BASE =
  process.env.REACT_APP_API_BASE?.replace(/\/+$/, "") || "http://127.0.0.1:8000";

export default function App() {
  // 页面状态管理
  const [location, setLocation] = useState("");
  const [trafficCondition, setTrafficCondition] = useState("normal");
  const [incidentDetected, setIncidentDetected] = useState(false);
  const [vehicleSpeed, setVehicleSpeed] = useState("");
  const [loading, setLoading] = useState(false);
  const [resp, setResp] = useState(null); // 响应
  const [history, setHistory] = useState([]); // 最近任务记录

  // 交互记录区域高度
  const [historyHeight, setHistoryHeight] = useState(260);

  // 任务指令数据
  const payload = useMemo(
    () => ({
      location: location.trim(),
      traffic_condition: trafficCondition,
      incident_detected: incidentDetected,
      vehicle_speed: vehicleSpeed === "" ? null : Number(vehicleSpeed),
      ts: Date.now(),
    }),
    [location, trafficCondition, incidentDetected, vehicleSpeed]
  );

  // 判断输入是否合法
  const isValid = useMemo(() => {
    if (!payload.location) return false;
    if (payload.vehicle_speed === null || Number.isNaN(payload.vehicle_speed))
      return false;
    if (payload.vehicle_speed < 0) return false;
    return true;
  }, [payload]);

  // 发送任务指令给子智能体
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!isValid) {
      setResp({ ok: false, message: "请完整填写表单并确保速度为非负数。" });
      return;
    }
    setLoading(true);
    setResp(null);
    try {
      const { data } = await axios.post(`${API_BASE}/events/autonomous_driving`, {
        location: payload.location,
        traffic_condition: payload.traffic_condition,
        incident_detected: payload.incident_detected,
        vehicle_speed: payload.vehicle_speed,
      });
      const message = typeof data === "object" && data?.status ? String(data.status) : "success";
      setResp({ ok: true, message });
      setHistory((h) =>
        [
          {
            id: cryptoRandomId(),
            at: new Date().toLocaleString(),
            ...payload,
            message,
          },
          ...h,
        ].slice(0, 100)
      );
    } catch (err) {
      setResp({
        ok: false,
        message:
          err?.response?.data?.detail
            ? `后端返回: ${JSON.stringify(err.response.data.detail)}`
            : "请求失败，请检查后端是否已启动或接口路径是否正确。",
      });
    } finally {
      setLoading(false);
    }
  };

  // WebSocket 接收任务完成反馈并更新UI
  useEffect(() => {
    const ws = new WebSocket(`${API_BASE.replace("http", "ws")}/ws`);
    ws.onmessage = (event) => {
      const data = JSON.parse(event.data);
      if (data.type === "task_complete") {
        // 任务完成反馈
        setHistory((h) => [
          {
            id: cryptoRandomId(),
            at: new Date().toLocaleString(),
            message: `任务完成：${data.status}`,
            result: data.data, // 任务结果数据
          },
          ...h,
        ]);
      }
    };
    return () => ws.close();
  }, []);

  // 拖拽条用于调整交互记录区域的高度
  const handleMouseDown = (e) => {
    const startY = e.clientY;
    const startHeight = historyHeight;

    const onMouseMove = (moveEvent) => {
      const diff = moveEvent.clientY - startY;
      setHistoryHeight(Math.max(150, startHeight + diff)); // 限制最小高度为150px
    };

    const onMouseUp = () => {
      window.removeEventListener("mousemove", onMouseMove);
      window.removeEventListener("mouseup", onMouseUp);
    };

    window.addEventListener("mousemove", onMouseMove);
    window.addEventListener("mouseup", onMouseUp);
  };

  return (
    <div className="page">
      <header className="page__header">
        <h1>自动驾驶 × 城市管理控制面板</h1>
        <p className="subtitle">左侧四个模块发起任务 / 右侧查看多智能体交互记录</p>
      </header>

      <main className="page__main">
        {/* 左侧 2×2 模块 + 右侧交互记录 */}
        <section className="modules">
          {/* 发送任务 */}
          <section className="card">
            <h2 className="card__title">发送任务</h2>
            <form onSubmit={handleSubmit} className="form">
              <div className="form__row">
                <label>
                  <span>Location</span>
                  <input
                    type="text"
                    value={location}
                    onChange={(e) => setLocation(e.target.value)}
                    placeholder="如：Main St."
                    required
                  />
                </label>

                <label>
                  <span>Traffic Condition</span>
                  <select
                    value={trafficCondition}
                    onChange={(e) => setTrafficCondition(e.target.value)}
                  >
                    <option value="normal">Normal</option>
                    <option value="heavy">Heavy</option>
                  </select>
                </label>
              </div>

              <div className="form__row">
                <label className="checkbox">
                  <input
                    type="checkbox"
                    checked={incidentDetected}
                    onChange={(e) => setIncidentDetected(e.target.checked)}
                  />
                  <span>Incident Detected</span>
                </label>

                <label>
                  <span>Vehicle Speed (km/h)</span>
                  <input
                    type="number"
                    min="0"
                    value={vehicleSpeed}
                    onChange={(e) => setVehicleSpeed(e.target.value)}
                    placeholder="例如 40"
                    required
                  />
                </label>
              </div>

              <button type="submit" className="btn" disabled={loading || !isValid}>
                {loading ? "Sending..." : "Send"}
              </button>
            </form>

            {resp && (
              <div className={`alert ${resp.ok ? "ok" : "err"}`}>
                <strong>Response:</strong> {resp.message}
              </div>
            )}
          </section>
        </section>
        {/* 第二行左侧模块：天气告警 / 停车位更新 */}
        <section className="modules">
          {/* 天气告警 */}
          <section className="card">
            <h2 className="card__title">天气告警</h2>
            <form
              className="form"
              onSubmit={async (e) => {
                e.preventDefault();
                try {
                  const { data } = await axios.post(
                    `${API_BASE}/events/weather_alert`,
                    { alert_type: weatherAlert, area: location }
                  );
                  setResp({ ok: true, message: data?.status ?? "ok" });
                  setHistory((h) => [
                    {
                      id: cryptoRandomId(),
                      at: new Date().toLocaleString(),
                      location,
                      alert_type: weatherAlert,
                      message: "Weather alert sent",
                    },
                    ...h,
                  ]);
                } catch (err) {
                  setResp({
                    ok: false,
                    message:
                      err?.response?.data?.detail
                        ? `后端返回: ${JSON.stringify(err.response.data.detail)}`
                        : "请求失败，请检查后端是否已启动或接口路径是否正确。",
                  });
                }
              }}
            >
              <label>
                <span>Alert Type</span>
                <input
                  type="text"
                  value={weatherAlert}
                  onChange={(e) => setWeatherAlert(e.target.value)}
                  placeholder="如：暴雨 / 大风"
                  required
                />
              </label>
              <label>
                <span>Area</span>
                <input
                  type="text"
                  value={location}
                  onChange={(e) => setLocation(e.target.value)}
                  placeholder="如：Beijing"
                  required
                />
              </label>
              <button type="submit" className="btn">Send Alert</button>
            </form>
          </section>

          {/* 停车位更新 */}
          <section className="card">
            <h2 className="card__title">停车位更新</h2>
            <form
              className="form"
              onSubmit={async (e) => {
                e.preventDefault();
                try {
                  const { data } = await axios.post(
                    `${API_BASE}/events/parking_update`,
                    { location: parkingLocation, available_spots: Number(availableSpots || 0) }
                  );
                  setResp({ ok: true, message: data?.status ?? "ok" });
                  setHistory((h) => [
                    {
                      id: cryptoRandomId(),
                      at: new Date().toLocaleString(),
                      location: parkingLocation,
                      available_spots: Number(availableSpots || 0),
                      message: "Parking spots updated",
                    },
                    ...h,
                  ]);
                } catch (err) {
                  setResp({
                    ok: false,
                    message:
                      err?.response?.data?.detail
                        ? `后端返回: ${JSON.stringify(err.response.data.detail)}`
                        : "请求失败，请检查后端是否已启动或接口路径是否正确。",
                  });
                }
              }}
            >
              <label>
                <span>Location</span>
                <input
                  type="text"
                  value={parkingLocation}
                  onChange={(e) => setParkingLocation(e.target.value)}
                  placeholder="停车场/区域"
                  required
                />
              </label>
              <label>
                <span>Available Spots</span>
                <input
                  type="number"
                  min="0"
                  value={availableSpots}
                  onChange={(e) => setAvailableSpots(e.target.value)}
                  placeholder="可用车位数"
                  required
                />
              </label>
              <button type="submit" className="btn">Update Parking</button>
            </form>
          </section>
        </section>

        {/* 第三行左侧模块：安全巡检 */}
        <section className="modules">
          <section className="card">
            <h2 className="card__title">安全巡检</h2>
            <form
              className="form"
              onSubmit={async (e) => {
                e.preventDefault();
                try {
                  const { data } = await axios.post(
                    `${API_BASE}/events/safety_monitor`,
                    { location, safety_status: safetyStatus }
                  );
                  setResp({ ok: true, message: data?.status ?? "ok" });
                  setHistory((h) => [
                    {
                      id: cryptoRandomId(),
                      at: new Date().toLocaleString(),
                      location,
                      safety_status: safetyStatus,
                      message: "Safety status sent",
                    },
                    ...h,
                  ]);
                } catch (err) {
                  setResp({
                    ok: false,
                    message:
                      err?.response?.data?.detail
                        ? `后端返回: ${JSON.stringify(err.response.data.detail)}`
                        : "请求失败，请检查后端是否已启动或接口路径是否正确。",
                  });
                }
              }}
            >
              <div className="form__row">
                <label>
                  <span>Location</span>
                  <input
                    type="text"
                    value={location}
                    onChange={(e) => setLocation(e.target.value)}
                    placeholder="如：Campus / Factory"
                    required
                  />
                </label>
                <label>
                  <span>Safety Status</span>
                  <select
                    value={safetyStatus}
                    onChange={(e) => setSafetyStatus(e.target.value)}
                  >
                    <option value="">请选择</option>
                    <option value="ok">OK</option>
                    <option value="warning">Warning</option>
                    <option value="incident">Incident</option>
                  </select>
                </label>
              </div>
              <button type="submit" className="btn">Send Safety Status</button>
            </form>
          </section>
        </section>

        {/* 右侧：交互记录（可拖拽调高） */}
        <section className="interaction-record">
          <section className="card">
            <h2 className="card__title">交互记录</h2>

            <div
              className="history scrollable"
              style={{ height: `${historyHeight}px` }}
            >
              {history.length === 0 ? (
                <p className="muted">暂无记录，等到推送消息后会显示。</p>
              ) : (
                <ul>
                  {history.map((h) => (
                    <li key={h.id}>
                      <div className="history__line">
                        {h.traffic_condition && (
                          <span className="pill">{h.traffic_condition}</span>
                        )}
                        {h.incident_detected && <span className="tag">incident</span>}
                        {h.alert_type && <span className="pill">weather</span>}
                        {typeof h.available_spots !== "undefined" && (
                          <span className="pill">parking</span>
                        )}
                        {h.safety_status && (
                          <span className="pill">{h.safety_status}</span>
                        )}
                        <span className="loc">{h.location || "-"}</span>
                      </div>
                      <div className="history__meta">
                        <span>{h.at}</span>
                        {"vehicle_speed" in h && (
                          <span>{h.vehicle_speed ?? "-"} km/h</span>
                        )}
                        {"available_spots" in h && (
                          <span>{h.available_spots} spots</span>
                        )}
                        <span>{h.message}</span>
                      </div>
                    </li>
                  ))}
                </ul>
              )}
            </div>

            {/* 拖拽条 */}
            <div className="drag-bar" onMouseDown={handleMouseDown} />
          </section>
        </section>
      </main>

      <footer className="page__footer">
        <span>
          API 基址：<code>{API_BASE}</code>
        </span>
      </footer>
    </div>
  );
}

// 工具方法：生成前端唯一ID
function cryptoRandomId() {
  if (window.crypto?.randomUUID) return window.crypto.randomUUID();
  return "id-" + Math.random().toString(36).slice(2);
}
