  };

  const isExecuteDisabled = () => {
    if (isLoading) return true;
    if (!sendMessage.trim()) return true;
    if (isMultiAgentMode) {
      return selectedAgents.length < 2;
    } else {
      return selectedAgents.length !== 1;
    }
  };

  return (
    <div className="task-input">
      <div className="input-header">
        <h3 className="input-title">‰ªªÂä°ËßÑÂàí / Task Planning</h3>
        <div className="input-subtitle">ËæìÂÖ•‰ªªÂä°ÊèèËø∞ÊàñÈÄâÊã©È¢ÑËÆæÊ®°Êùø</div>
