import React, { useState } from 'react';
import './HomePage.css';
import { useEventContext } from '../contexts/EventContext';
import AutonomousDrivingCard from '../components/AutonomousDrivingCard';
import ParkingUpdateCard from '../components/ParkingUpdateCard';
import SafetyInspectionCard from '../components/SafetyInspectionCard';
import WeatherAlertCard from '../components/WeatherAlertCard';
import InteractionHistory from '../components/InteractionHistory';

const HomePage = () => {
  const { state, sendAgentAction, readyState, clearEvents } = useEventContext();
  const { events } = state;

  const [formData, setFormData] = useState({
    autonomous_driving: {
      start_location: 'A',
      end_location: 'B',
      passengers: 2,
    },
    weather_alert: {
      location: 'City Center',
      alert_type: 'heavy_rain',
      severity: 8,
    },
    parking_update: {
      location: 'Downtown Garage',
      available_spots: 150,
    },
    safety_inspection: {
      location: 'Bridge B',
      require_human_intervention: true,
    },
  });

  const generateReport = async () => {
    console.log("Calling generateReport");
    const lastFiveEvents = events.slice(0, 5);
    try {
      const backendPort = process.env.REACT_APP_BACKEND_PORT || 8006;
      const response = await fetch(`http://localhost:${backendPort}/api/generate-report`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ events: lastFiveEvents }),
      });
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
    } catch (error) {
      console.error("Error generating report:", error);
    }
  };

  const handleSend = (task) => {
    const payload = {
      ...formData[task],
    };
    sendAgentAction('dsl', payload, task);
  };

  const handleFormUpdate = (form, field, value) => {
    setFormData((prev) => ({
      ...prev,
      [form]: {
        ...prev[form],
        [field]: value,
      },
    }));
  };

  return (
    <div className="homepage">
      <header className="homepage__header">
        <h1 className="homepage__title">Agent Interaction Platform</h1>
        <p className="homepage__subtitle">
          Monitor and control autonomous systems in real-time
        </p>
      </header>
      <main className="homepage__main">
        <div className="card-grid">
          <AutonomousDrivingCard
            data={formData.autonomous_driving}
            onUpdate={(e) =>
              handleFormUpdate('autonomous_driving', e.target.name, e.target.value)
            }
            onSend={() => handleSend('autonomous_driving')}
          />
          <WeatherAlertCard
            data={formData.weather_alert}
            onUpdate={(e) =>
              handleFormUpdate('weather_alert', e.target.name, e.target.value)
            }
            onSend={() => handleSend('weather_alert')}
          />
          <ParkingUpdateCard
            data={formData.parking_update}
            onUpdate={(e) =>
              handleFormUpdate('parking_update', e.target.name, e.target.value)
            }
            onSend={() => handleSend('parking_update')}
          />
          <SafetyInspectionCard
            data={formData.safety_inspection}
            onUpdate={(e) =>
              handleFormUpdate(
                'safety_inspection',
                e.target.name,
                e.target.type === 'checkbox' ? e.target.checked : e.target.value
              )
            }
            onSend={() => handleSend('safety_inspection')}
          />
        </div>
        <InteractionHistory events={events} onClear={clearEvents} onGenerateReport={generateReport} readyState={readyState} />
      </main>
    </div>
  );
};

export default HomePage;
