import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [location, setLocation] = useState("");
  const [trafficCondition, setTrafficCondition] = useState("normal");
  const [incidentDetected, setIncidentDetected] = useState(false);
  const [vehicleSpeed, setVehicleSpeed] = useState("");
  const [loading, setLoading] = useState(false);
  const [responseMessage, setResponseMessage] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setResponseMessage("");
    setErrorMessage("");

    // Basic form validation
    if (!location || !vehicleSpeed || isNaN(vehicleSpeed)) {
      setErrorMessage("Please fill out all fields correctly.");
      setLoading(false);
      return;
    }

    const data = {
      location,
      traffic_condition: trafficCondition,
      incident_detected: incidentDetected,
      vehicle_speed: vehicleSpeed,
    };

    try {
      const response = await axios.post(
        "http://127.0.0.1:8000/events/autonomous_driving",
        data
      );
      setResponseMessage(response.data.status);
    } catch (error) {
      setResponseMessage("Error: Could not complete the request.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <h1 className="title">自动驾驶 × 城市管理控制面板</h1>

      {/* Error Message */}
      {errorMessage && <div className="error-message">{errorMessage}</div>}

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Location</label>
          <input
            type="text"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            placeholder="Enter location"
            required
          />
        </div>

        <div className="form-group">
          <label>Traffic Condition</label>
          <select
            value={trafficCondition}
            onChange={(e) => setTrafficCondition(e.target.value)}
          >
            <option value="normal">Normal</option>
            <option value="heavy">Heavy</option>
          </select>
        </div>

        <div className="form-group">
          <label>Incident Detected</label>
          <input
            type="checkbox"
            checked={incidentDetected}
            onChange={(e) => setIncidentDetected(e.target.checked)}
          />
        </div>

        <div className="form-group">
          <label>Vehicle Speed (km/h)</label>
          <input
            type="number"
            value={vehicleSpeed}
            onChange={(e) => setVehicleSpeed(e.target.value)}
            placeholder="Enter speed"
            required
            min="0"  // Minimum speed is 0 km/h
          />
        </div>

        <button type="submit" className="submit-btn" disabled={loading}>
          {loading ? "Loading..." : "Send"}
        </button>
      </form>

      {/* Response Message */}
      {responseMessage && (
        <div className="response-message">
          <strong>Response:</strong> {responseMessage}
        </div>
      )}
    </div>
  );
}

export default App;
