import React, { useState, useMemo } from "react";
import axios from "axios";
import "./App.css";

const API_BASE =
  process.env.REACT_APP_API_BASE?.replace(/\/+$/, "") || "http://127.0.0.1:8000";

export default function App() {
  const [location, setLocation] = useState("");
  const [trafficCondition, setTrafficCondition] = useState("normal");
  const [incidentDetected, setIncidentDetected] = useState(false);
  const [vehicleSpeed, setVehicleSpeed] = useState("");
  const [loading, setLoading] = useState(false);
  const [resp, setResp] = useState(null); // { ok: boolean, message: string }
  const [history, setHistory] = useState([]); // 最近发送记录

  // 新增的状态：天气告警、停车位更新、安全巡检
  const [weatherAlert, setWeatherAlert] = useState("");
  const [parkingLocation, setParkingLocation] = useState("");
  const [availableSpots, setAvailableSpots] = useState("");
  const [safetyStatus, setSafetyStatus] = useState("");

  const payload = useMemo(
    () => ({
      location: location.trim(),
      traffic_condition: trafficCondition,
      incident_detected: incidentDetected,
      vehicle_speed: vehicleSpeed === "" ? null : Number(vehicleSpeed),
      ts: Date.now(),
    }),
    [location, trafficCondition, incidentDetected, vehicleSpeed]
  );

  const isValid = useMemo(() => {
    if (!payload.location) return false;
    if (payload.vehicle_speed === null || Number.isNaN(payload.vehicle_speed))
      return false;
    if (payload.vehicle_speed < 0) return false;
    return true;
  }, [payload]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!isValid) {
      setResp({ ok: false, message: "请完整填写表单并确保速度为非负数。" });
      return;
    }
    setLoading(true);
    setResp(null);
    try {
      const { data } = await axios.post(
        `${API_BASE}/events/autonomous_driving`,
        {
          location: payload.location,
          traffic_condition: payload.traffic_condition,
          incident_detected: payload.incident_detected,
          vehicle_speed: payload.vehicle_speed,
        }
      );
      const message =
        typeof data === "object" && data?.status
          ? String(data.status)
          : "success";
      setResp({ ok: true, message });
      setHistory((h) =>
        [
          {
            id: cryptoRandomId(),
            at: new Date().toLocaleString(),
            ...payload,
            message,
          },
          ...h,
        ].slice(0, 8)
      );
    } catch (err) {
      setResp({
        ok: false,
        message:
          err?.response?.data?.detail
            ? `后端返回: ${JSON.stringify(err.response.data.detail)}`
            : "请求失败，请检查后端是否已启动或接口路径是否正确。",
      });
    } finally {
      setLoading(false);
    }
  };

  // 发送天气告警
  const handleWeatherAlertSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setResp(null);
    try {
      const { data } = await axios.post(
        `${API_BASE}/events/weather_alert`,
        {
          alert_type: weatherAlert,
          area: location,
        }
      );
      setResp({ ok: true, message: data.status });
    } catch (err) {
      setResp({
        ok: false,
        message:
          err?.response?.data?.detail
            ? `后端返回: ${JSON.stringify(err.response.data.detail)}`
            : "请求失败，请检查后端是否已启动或接口路径是否正确。",
      });
    } finally {
      setLoading(false);
    }
  };

  // 发送停车位更新
  const handleParkingUpdateSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setResp(null);
    try {
      const { data } = await axios.post(
        `${API_BASE}/events/parking_update`,
        {
          location: parkingLocation,
          available_spots: availableSpots,
        }
      );
      setResp({ ok: true, message: data.status });
    } catch (err) {
      setResp({
        ok: false,
        message:
          err?.response?.data?.detail
            ? `后端返回: ${JSON.stringify(err.response.data.detail)}`
            : "请求失败，请检查后端是否已启动或接口路径是否正确。",
      });
    } finally {
      setLoading(false);
    }
  };

  // 发送安全巡检更新
  const handleSafetyMonitorSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setResp(null);
    try {
      const { data } = await axios.post(
        `${API_BASE}/events/safety_monitor`,
        {
          location: location,
          safety_status: safetyStatus,
        }
      );
      setResp({ ok: true, message: data.status });
    } catch (err) {
      setResp({
        ok: false,
        message:
          err?.response?.data?.detail
            ? `后端返回: ${JSON.stringify(err.response.data.detail)}`
            : "请求失败，请检查后端是否已启动或接口路径是否正确。",
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="page">
      <header className="page__header">
        <h1>自动驾驶 × 城市管理控制面板</h1>
        <p className="subtitle">
          通过 API 向后端投递任务（/events/autonomous_driving），查看响应与最近记录
        </p>
      </header>

      <main className="grid">
        <section className="card">
          <h2 className="card__title">发送任务</h2>
          {/* 任务表单 */}
          <form onSubmit={handleSubmit} className="form">
            <div className="form__row">
              <label>
                <span>Location</span>
                <input
                  type="text"
                  value={location}
                  onChange={(e) => setLocation(e.target.value)}
                  placeholder="如：Main St."
                  required
                />
              </label>

              <label>
                <span>Traffic Condition</span>
                <select
                  value={trafficCondition}
                  onChange={(e) => setTrafficCondition(e.target.value)}
                >
                  <option value="normal">Normal</option>
                  <option value="heavy">Heavy</option>
                </select>
              </label>
            </div>

            <div className="form__row">
              <label className="checkbox">
                <input
                  type="checkbox"
                  checked={incidentDetected}
                  onChange={(e) => setIncidentDetected(e.target.checked)}
                />
                <span>Incident Detected</span>
              </label>

              <label>
                <span>Vehicle Speed (km/h)</span>
                <input
                  type="number"
                  min="0"
                  value={vehicleSpeed}
                  onChange={(e) => setVehicleSpeed(e.target.value)}
                  placeholder="例如 40"
                  required
                />
              </label>
            </div>

            <button type="submit" className="btn" disabled={loading || !isValid}>
              {loading ? "Sending..." : "Send"}
            </button>
          </form>

          {resp && (
            <div className={`alert ${resp.ok ? "ok" : "err"}`}>
              <strong>Response:</strong> {resp.message}
            </div>
          )}
        </section>

        {/* 新增的三个API表单：天气告警、停车位更新、安全巡检 */}
        <section className="card">
          <h2 className="card__title">天气告警</h2>
          <form onSubmit={handleWeatherAlertSubmit} className="form">
            <label>
              <span>Alert Type</span>
              <input
                type="text"
                value={weatherAlert}
                onChange={(e) => setWeatherAlert(e.target.value)}
                placeholder="例如暴雨"
                required
              />
            </label>
            <label>
              <span>Area</span>
              <input
                type="text"
                value={location}
                onChange={(e) => setLocation(e.target.value)}
                placeholder="例如北京"
                required
              />
            </label>
            <button type="submit" className="btn" disabled={loading}>
              {loading ? "Sending..." : "Send Alert"}
            </button>
          </form>
        </section>

        <section className="card">
          <h2 className="card__title">停车位更新</h2>
          <form onSubmit={handleParkingUpdateSubmit} className="form">
            <label>
              <span>Location</span>
              <input
                type="text"
                value={parkingLocation}
                onChange={(e) => setParkingLocation(e.target.value)}
                placeholder="停车地点"
                required
              />
            </label>
            <label>
              <span>Available Spots</span>
              <input
                type="number"
                value={availableSpots}
                onChange={(e) => setAvailableSpots(e.target.value)}
                placeholder="可用车位数"
                required
              />
            </label>
            <button type="submit" className="btn" disabled={loading}>
              {loading ? "Sending..." : "Update Parking"}
            </button>
          </form>
        </section>

        <section className="card">
          <h2 className="card__title">安全巡检</h2>
          <form onSubmit={handleSafetyMonitorSubmit} className="form">
            <label>
              <span>Location</span>
              <input
                type="text"
                value={location}
                onChange={(e) => setLocation(e.target.value)}
                placeholder="例如校园"
                required
              />
            </label>
            <label>
              <span>Safety Status</span>
              <select
                value={safetyStatus}
                onChange={(e) => setSafetyStatus(e.target.value)}
              >
                <option value="ok">OK</option>
                <option value="warning">Warning</option>
                <option value="incident">Incident</option>
              </select>
            </label>
            <button type="submit" className="btn" disabled={loading}>
              {loading ? "Sending..." : "Send Safety Status"}
            </button>
          </form>
        </section>
      </main>
    </div>
  );
}

function cryptoRandomId() {
  // 浏览器环境有 crypto; Node 里也可 fallback
  if (window.crypto?.randomUUID) return window.crypto.randomUUID();
  return "id-" + Math.random().toString(36).slice(2);
}
