      dsl: step.dsl,
      agents: step.participants.map(name => ({ name, role: name === 'Master Agent' ? 'Master Agent' : 'Sub Agent' })),
      message: title,
      description: description,
      timestamp: step.timestamp || new Date().toLocaleString(),
      status: step.status || 'active',
