import React, { useState, useEffect } from 'react';
import './AgentInteractionFlow.css';

const AgentInteractionFlow = ({ events, selectedEvent }) => {
  const [interactionFlow, setInteractionFlow] = useState([]);

  // Êô∫ËÉΩ‰ΩìÈÖçÁΩÆ
  const agentConfig = {
    'master_agent': { name: '‰∏ªÊô∫ËÉΩ‰Ωì', icon: 'üéØ', color: '#ff6b6b' },
    'traffic_manager': { name: '‰∫§ÈÄöÁÆ°ÁêÜ', icon: 'üö¶', color: '#4ecdc4' },
    'safety_agent': { name: 'ÂÆâÂÖ®Ê£ÄÊµã', icon: 'üõ°Ô∏è', color: '#45b7d1' },
    'weather_agent': { name: 'Â§©Ê∞îÈ¢ÑË≠¶', icon: 'üå¶Ô∏è', color: '#96ceb4' },
    'parking_agent': { name: 'ÂÅúËΩ¶ÁÆ°ÁêÜ', icon: 'üÖøÔ∏è', color: '#feca57' },
    'autonomous_driving': { name: 'Ëá™Âä®È©æÈ©∂', icon: 'üöó', color: '#ff9ff3' }
  };

  // Ëß£Êûê‰∫ã‰ª∂ÁîüÊàê‰∫§‰∫íÊµÅÁ®ã
  useEffect(() => {
    if (!selectedEvent || !events) return;

    const relatedEvents = getRelatedEvents(selectedEvent, events);
    const flow = generateInteractionFlow(selectedEvent, relatedEvents);
    setInteractionFlow(flow);
  }, [selectedEvent, events]);

  const getRelatedEvents = (sentEvent, allEvents) => {
    if (!sentEvent || !sentEvent.timestamp) return [];

    const timeWindow = 10 * 60 * 1000; // 10ÂàÜÈíü
    const sentTime = new Date(sentEvent.timestamp);

    return allEvents.filter(event => {
      if (!event.timestamp || event.id === sentEvent.id) return false;

      const eventTime = new Date(event.timestamp);
      const timeDiff = Math.abs(eventTime.getTime() - sentTime.getTime());

      return timeDiff <= timeWindow;
    }).sort((a, b) => new Date(a.timestamp) - new Date(b.timestamp));
  };

  const generateInteractionFlow = (sentEvent, relatedEvents) => {
    const flow = [];

    // 1. ÂàùÂßã‰∫ã‰ª∂
    const eventType = sentEvent?.type || sentEvent?.event_type || 'Êú™Áü•‰∫ã‰ª∂';
    const eventTitle = getEventTitle(sentEvent);

    flow.push({
      id: 'initial',
      type: 'event',
      agent: getAgentFromEvent(sentEvent),
      message: `Ëß¶Âèë‰∫ã‰ª∂: ${eventTitle}`,
      timestamp: sentEvent?.timestamp,
      status: 'completed'
    });

    // 2. Â∑•‰ΩúÊµÅ‰∫ã‰ª∂
    relatedEvents.forEach(event => {
      const agent = getAgentFromEvent(event);
      const message = getEventMessage(event);

      flow.push({
        id: event.id || `event_${Date.now()}`,
        type: event.type,
        agent: agent,
        message: message,
        timestamp: event.timestamp,
        status: 'completed',
        details: event.payload
      });
    });

    return flow;
  };

  const getEventTitle = (event) => {
    if (!event) return 'Êú™Áü•‰∫ã‰ª∂';

    const eventType = event.type || event.event_type || '';
    const payload = event.payload || {};

    switch (eventType) {
      case 'weather_alert':
        return `Â§©Ê∞îÈ¢ÑË≠¶ - ${payload.alert_type || 'Êú™Áü•Á±ªÂûã'}`;
      case 'autonomous_driving':
        return `Ëá™Âä®È©æÈ©∂ - ${payload.start_location || 'Ëµ∑ÁÇπ'} ‚Üí ${payload.end_location || 'ÁªàÁÇπ'}`;
      case 'parking_update':
        return `ÂÅúËΩ¶Êõ¥Êñ∞ - ${payload.location || 'Êú™Áü•‰ΩçÁΩÆ'}`;
      case 'safety_inspection':
        return `ÂÆâÂÖ®Ê£ÄÊü• - ${payload.location || 'Êú™Áü•‰ΩçÁΩÆ'}`;
      default:
        return eventType || 'Êú™Áü•‰∫ã‰ª∂';
    }
  };

  const getAgentFromEvent = (event) => {
    if (!event) return 'master_agent';

    const eventType = event.type || event.event_type || '';
    const payload = event.payload || {};

    switch (eventType) {
      case 'weather_alert':
        return 'weather_agent';
      case 'autonomous_driving':
        return 'autonomous_driving';
      case 'parking_update':
        return 'parking_agent';
      case 'safety_inspection':
        return 'safety_agent';
      case 'workflow_start':
      case 'workflow_complete':
        return 'master_agent';
      case 'main_task_result':
        return 'master_agent';
      case 'coordination_start':
      case 'coordination_result':
        return 'master_agent';
      case 'agent_response':
        // ‰ªépayload‰∏≠Ëé∑ÂèñÊô∫ËÉΩ‰Ωì‰ø°ÊÅØ
        if (typeof payload === 'object' && payload !== null && payload.agent) {
          const agentName = payload.agent;
          if (typeof agentName === 'string') {
            if (agentName.includes('‰∫§ÈÄöÁÆ°ÁêÜ')) return 'traffic_manager';
            if (agentName.includes('ÂÆâÂÖ®')) return 'safety_agent';
            if (agentName.includes('Â§©Ê∞î')) return 'weather_agent';
            if (agentName.includes('ÂÅúËΩ¶')) return 'parking_agent';
            if (agentName.includes('Ëá™Âä®È©æÈ©∂')) return 'autonomous_driving';
          }
        }
        return 'master_agent';
      case 'agent_error':
        return 'master_agent';
      default:
        // ‰ªépayloadÂà§Êñ≠
        if (typeof payload === 'string' && payload.length > 0) {
          if (payload.includes('TrafficManager') || payload.includes('‰∫§ÈÄöÁÆ°ÁêÜ')) return 'traffic_manager';
          if (payload.includes('SafetyAgent') || payload.includes('ÂÆâÂÖ®')) return 'safety_agent';
          if (payload.includes('WeatherAgent') || payload.includes('Â§©Ê∞î')) return 'weather_agent';
          if (payload.includes('ParkingAgent') || payload.includes('ÂÅúËΩ¶')) return 'parking_agent';
          if (payload.includes('AutonomousDriving') || payload.includes('Ëá™Âä®È©æÈ©∂')) return 'autonomous_driving';
        } else if (typeof payload === 'object' && payload !== null) {
          // Â¶ÇÊûúÊòØÂØπË±°ÔºåÂ∞ùËØï‰ªéÂØπË±°Â±ûÊÄß‰∏≠Ëé∑Âèñ‰ø°ÊÅØ
          const payloadStr = JSON.stringify(payload);
          if (payloadStr.includes('TrafficManager') || payloadStr.includes('‰∫§ÈÄöÁÆ°ÁêÜ')) return 'traffic_manager';
          if (payloadStr.includes('SafetyAgent') || payloadStr.includes('ÂÆâÂÖ®')) return 'safety_agent';
          if (payloadStr.includes('WeatherAgent') || payloadStr.includes('Â§©Ê∞î')) return 'weather_agent';
          if (payloadStr.includes('ParkingAgent') || payloadStr.includes('ÂÅúËΩ¶')) return 'parking_agent';
          if (payloadStr.includes('AutonomousDriving') || payloadStr.includes('Ëá™Âä®È©æÈ©∂')) return 'autonomous_driving';
        }

        return 'master_agent';
    }
  };

  const getEventMessage = (event) => {
    const eventType = event.type || '';
    const payload = event.payload || {};

    switch (eventType) {
      case 'workflow_start':
        return 'Â∑•‰ΩúÊµÅÂêØÂä®';
      case 'main_task_result':
        return '‰∏ªÊô∫ËÉΩ‰ΩìÂçèË∞ÉÂÆåÊàê';
      case 'coordination_start':
        return 'Â≠êÊô∫ËÉΩ‰ΩìÂçèË∞ÉÂºÄÂßã';
      case 'coordination_result':
        return 'Â≠êÊô∫ËÉΩ‰ΩìÂçèË∞ÉÂÆåÊàê';
      case 'workflow_complete':
        return 'Â∑•‰ΩúÊµÅÊâßË°åÂÆåÊàê';
      case 'agent_response':
        // ‰ªépayload‰∏≠Ëé∑ÂèñÊô∫ËÉΩ‰ΩìÂìçÂ∫î‰ø°ÊÅØ
        if (typeof payload === 'object' && payload !== null && payload.agent) {
          return `${payload.agent}ÂìçÂ∫î`;
        }
        return 'Êô∫ËÉΩ‰ΩìÂìçÂ∫î';
      case 'agent_error':
        return 'Êô∫ËÉΩ‰ΩìÊâßË°åÈîôËØØ';
      default:
        if (typeof payload === 'string' && payload.length > 0) {
          if (payload.includes('Êö¥Èõ®') || payload.includes('heavy_rain')) {
            if (payload.includes('TrafficManager')) return 'Êö¥Èõ®Â§©Ê∞î‰∫§ÈÄöÁ¥ßÊÄ•ÂìçÂ∫î';
            if (payload.includes('SafetyAgent')) return 'Êö¥Èõ®Â§©Ê∞îÂÆâÂÖ®Á¥ßÊÄ•ÂìçÂ∫î';
          }
        } else if (typeof payload === 'object' && payload !== null) {
          const payloadStr = JSON.stringify(payload);
          if (payloadStr.includes('Êö¥Èõ®') || payloadStr.includes('heavy_rain')) {
            if (payloadStr.includes('TrafficManager')) return 'Êö¥Èõ®Â§©Ê∞î‰∫§ÈÄöÁ¥ßÊÄ•ÂìçÂ∫î';
            if (payloadStr.includes('SafetyAgent')) return 'Êö¥Èõ®Â§©Ê∞îÂÆâÂÖ®Á¥ßÊÄ•ÂìçÂ∫î';
          }
        }
        return 'Êô∫ËÉΩ‰ΩìÂìçÂ∫î';
    }
  };

  if (!selectedEvent || interactionFlow.length === 0) {
    return (
      <div className="agent-interaction-flow">
        <div className="flow-placeholder">
          <div className="placeholder-icon">ü§ñ</div>
          <div className="placeholder-text">ÈÄâÊã©Â∑¶‰æß‰∫ã‰ª∂Êü•ÁúãÊô∫ËÉΩ‰Ωì‰∫§‰∫íÊµÅÁ®ã</div>
        </div>
      </div>
    );
  }

  return (
    <div className="agent-interaction-flow">
      <div className="flow-header">
        <h3>ü§ñ Êô∫ËÉΩ‰Ωì‰∫§‰∫íÊµÅÁ®ã</h3>
        <div className="flow-info">
          ÂÖ± {interactionFlow.length} ‰∏™Ê≠•È™§
        </div>
      </div>

      <div className="flow-timeline">
        {interactionFlow.map((step, index) => {
          const agent = agentConfig[step.agent] || { name: 'Êú™Áü•Êô∫ËÉΩ‰Ωì', icon: '‚ùì', color: '#ccc' };

          return (
            <div key={step.id} className="timeline-step completed">
              <div className="step-indicator" style={{ backgroundColor: agent.color }}>
                <div className="step-icon">{agent.icon}</div>
              </div>
              <div className="step-content">
                <div className="step-title">{step.message}</div>
                <div className="step-agent">{agent.name}</div>
                <div className="step-time">
                  {step.timestamp ? new Date(step.timestamp).toLocaleTimeString() : ''}
                </div>
                {step.details && (
                  <div className="step-details">
                    {typeof step.details === 'string' ? step.details.substring(0, 100) + '...' : 'ËØ¶ÁªÜ‰ø°ÊÅØ'}
                  </div>
                )}
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default AgentInteractionFlow;
